// src/pages/ActionPage.js
import React, { useState } from 'react';
import styled from 'styled-components';
import { useSearchParams } from 'react-router-dom';
import axios from 'axios';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 20px;
`;

const Button = styled.button`
  padding: 10px;
  font-size: 1rem;
  background-color: #007bff;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  margin: 10px;

  &:hover {
    background-color: #0056b3;
  }
`;

const Input = styled.input`
  padding: 10px;
  font-size: 1rem;
  border: 1px solid #ccc;
  border-radius: 5px;
  margin: 10px;
  width: 300px;
`;

const ActionPage = () => {
  const [searchParams] = useSearchParams();
  const userId = searchParams.get('userId');
  const [phoneNumber, setPhoneNumber] = useState('');

  const handleSendSMS = async () => {
    try {
      await axios.get('http://localhost:5000/send-sms', {
        params: {
          userId,
          userPhoneNumber: phoneNumber
        }
      });
      alert('SMS sent successfully');
    } catch (error) {
      console.error('Error sending SMS:', error);
      alert('Failed to send SMS');
    }
  };

  const handleSendWhatsApp = async () => {
    try {
      await axios.get('http://localhost:5000/send-whatsapp', {
        params: {
          userId,
          userPhoneNumber: phoneNumber
        }
      });
      alert('WhatsApp message sent successfully');
    } catch (error) {
      console.error('Error sending WhatsApp message:', error);
      alert('Failed to send WhatsApp message');
    }
  };

  const handleSendVoiceNote = async () => {
    try {
      await axios.get('http://localhost:5000/send-voice-note', {
        params: {
          userId,
          userPhoneNumber: phoneNumber
        }
      });
      alert('Voice note sent successfully');
    } catch (error) {
      console.error('Error sending voice note:', error);
      alert('Failed to send voice note');
    }
  };

  return (
    <Container>
      <h1>Choose an Action</h1>
      <Input
        type="text"
        placeholder="Enter phone number"
        value={phoneNumber}
        onChange={(e) => setPhoneNumber(e.target.value)}
      />
      <Button onClick={handleSendSMS}>Send SMS</Button>
      <Button onClick={handleSendWhatsApp}>Send WhatsApp</Button>
      <Button onClick={handleSendVoiceNote}>Send Voice Note</Button>
    </Container>
  );
};

export default ActionPage;
